        let query = format!(r"INSERT INTO {:}.ch2 (channel_cid, pb_data, debug_data) VALUES (?, ?, ?)",&spool.database);
        let p = Params::Positional(vec![self.channel_cid.clone().into(), self.pb_data.clone().into(), self.debug_data.clone().into()]);

        conn.exec_iter(
            query, p
        ).await?;

