pub async fn channel_msg_like_mass_insert(arr :&Vec<ChannelMsgLike>, spool: &SPool) -> Result<(),MyError> {
    let mut conn = spool.pool.get_conn().await?;
    if arr.len() == 0 {
        return Err(MyError::EmptySql)
    }
     let mut insert_fields = "(message_gid, profile_id, created_time)";

     let mut vals_str = "(?, ?, ?), ".repeat(arr.len());
    let vals_str = &vals_str[0..(vals_str.len()-2)];

    let query = format!(r"INSERT INTO {:}.tweet {:} VALUES {:}", &spool.database, insert_fields, vals_str);

    let mut arr_vals = vec![];
    for ar in arr {
        arr_vals.push(ar.message_gid.clone().into());
        arr_vals.push(ar.profile_id.clone().into());
        arr_vals.push(ar.created_time.clone().into());}

    let p = Params::Positional(arr_vals);

    println!("{} - {:?}", &query, &p);

    let qr = conn.exec_iter(
        query, p
    ).await?;

    Ok(())
}

// Index
pub async fn get_channel_msg_like(message_gid: u64,profile_id: u32, spool: &SPool) -> Result<ChannelMsgLike,MyError> {
	let m = ChannelMsgLikeSelector::new()
		.message_gid_eq(message_gid)
		.and_profile_id_eq(profile_id)
		.get_row(spool).await?;
	Ok(m)
}
