#[derive(Default, Clone, PartialEq, Eq, Debug)]
pub struct File  { // file
    pub file_gid: u64,
    pub pb_data: Vec<u8>,
    pub debug_data: String,
}

impl FromRow for File {
    fn from_row_opt(row: Row) -> Result<Self, FromRowError>
    where
        Self: Sized,
    {
        Ok(File  {
            file_gid: row.get(0).unwrap_or_default(),
            pb_data: row.get(1).unwrap_or_default(),
            debug_data: row.get(2).unwrap_or_default(),
        })
    }
}

impl File {
    pub async fn insert(&self, spool: &SPool) -> Result<File,MyError> {
        let mut conn = spool.pool.get_conn().await?;

        let query = format!(r"INSERT INTO {:}.file (file_gid, pb_data, debug_data) VALUES (?, ?, ?)",&spool.database);
        let p = Params::Positional(vec![self.file_gid.clone().into(), self.pb_data.clone().into(), self.debug_data.clone().into()]);

        conn.exec_iter(
            query, p
        ).await?;

        let cp = self.clone();
        Ok(cp)
    }

    // todo add replace with coping insert after it's complete

    pub async fn update(&self, spool: &SPool) -> Result<(),MyError> {
        let mut conn = spool.pool.get_conn().await?;
        let query = format!(r"UPDATE `{:}`.file` SET pb_data = ?, debug_data = ? WHERE file_gid = ? ", &spool.database);
        let p = Params::Positional(vec![self.pb_data.clone().into(), self.debug_data.clone().into(),  self.file_gid.clone().into() ]);

        let qr = conn.exec_iter(
            query, p
        ).await?;

        Ok(())
    }

    pub async fn delete(&self, spool: &SPool) -> Result<(),MyError> {
        let mut conn = spool.pool.get_conn().await?;

        let query = format!(r"DELETE FROM `{:}`.file` WHERE file_gid = ? ", &spool.database);
        let p = Params::Positional(vec![self.file_gid.clone().into()]);

        conn.exec_drop(
            query, p
        ).await?;

        Ok(())
    }
}


