pub async fn profile_follower_mass_insert(arr :&Vec<ProfileFollower>, spool: &SPool) -> Result<(),MyError> {
    let mut conn = spool.pool.get_conn().await?;
    if arr.len() == 0 {
        return Err(MyError::EmptySql)
    }
     let mut insert_fields = "(profile_id, channel_id, follow_gid)";

     let mut vals_str = "(?, ?, ?), ".repeat(arr.len());
    let vals_str = &vals_str[0..(vals_str.len()-2)];

    let query = format!(r"INSERT INTO {:}.tweet {:} VALUES {:}", &spool.database, insert_fields, vals_str);

    let mut arr_vals = vec![];
    for ar in arr {
        arr_vals.push(ar.profile_id.clone().into());
        arr_vals.push(ar.channel_id.clone().into());
        arr_vals.push(ar.follow_gid.clone().into());}

    let p = Params::Positional(arr_vals);

    println!("{} - {:?}", &query, &p);

    let qr = conn.exec_iter(
        query, p
    ).await?;

    Ok(())
}

// Index
pub async fn get_profile_follower(profile_id: u32,channel_id: u32,follow_gid: u64, spool: &SPool) -> Result<ProfileFollower,MyError> {
	let m = ProfileFollowerSelector::new()
		.profile_id_eq(profile_id)
		.and_channel_id_eq(channel_id)
		.and_follow_gid_eq(follow_gid)
		.get_row(spool).await?;
	Ok(m)
}
