    pub fn session_hash_in (&mut self, val: Vec<&str> ) -> &mut Self {
		let len = val.len();
        if len == 0 {
            return self
        }

        let mut marks = "?,".repeat(len);
        marks.remove(marks.len()-1);

		let arr = val.iter().map(|v|v.into()).collect();

        let w = WhereInClause{
			condition: format!(" session_hash IN ({})", marks),
            args: arr,
        };
        self.q.wheres_ins.push(w);
        self
    }

