pub async fn session_mass_insert(arr :&Vec<Session>, spool: &SPool) -> Result<(),MyError> {
    let mut conn = spool.pool.get_conn().await?;
    if arr.len() == 0 {
        return Err(MyError::EmptySql)
    }
     let mut insert_fields = "(session_hash, user_cid, pb_data, debug_data)";

     let mut vals_str = "(?, ?, ?, ?), ".repeat(arr.len());
    let vals_str = &vals_str[0..(vals_str.len()-2)];

    let query = format!(r"INSERT INTO {:}.tweet {:} VALUES {:}", &spool.database, insert_fields, vals_str);

    let mut arr_vals = vec![];
    for ar in arr {
        arr_vals.push(ar.session_hash.clone().into());
        arr_vals.push(ar.user_cid.clone().into());
        arr_vals.push(ar.pb_data.clone().into());
        arr_vals.push(ar.debug_data.clone().into());}

    let p = Params::Positional(arr_vals);

    println!("{} - {:?}", &query, &p);

    let qr = conn.exec_iter(
        query, p
    ).await?;

    Ok(())
}

// Index
pub async fn get_session(session_hash: &str, spool: &SPool) -> Result<Session,MyError> {
	let m = SessionSelector::new()
		.session_hash_eq(session_hash)
		.get_row(spool).await?;
	Ok(m)
}
