#[derive(Default, Clone, PartialEq, Eq, Debug)]
pub struct Tweet  { // tweet
    pub tweet_id: u64,
    pub created_time: u64,
    pub text_body: String,
}

impl FromRow for Tweet {
    fn from_row_opt(row: Row) -> Result<Self, FromRowError>
    where
        Self: Sized,
    {
        Ok(Tweet  {
            tweet_id: row.get(0).unwrap_or_default(),
            created_time: row.get(1).unwrap_or_default(),
            text_body: row.get(2).unwrap_or_default(),
        })
    }
}

impl Tweet {
    pub async fn insert(&self, spool: &SPool) -> Result<Tweet,MyError> {
        let mut conn = spool.pool.get_conn().await?;

        let query = format!(r"INSERT INTO {:}.tweet (created_time, text_body) VALUES (?, ?)",&spool.database);
        let p = Params::Positional(vec![self.created_time.clone().into(), self.text_body.clone().into()]);

        let qr = conn.exec_iter(
            query, p
        ).await?;

        let mut cp = self.clone();
        cp.tweet_id = qr.last_insert_id().unwrap() as u64;
        Ok(cp)
    }


    // [[ for template: this code is copy of insert with 'insert' changed to 'replace' ]]
    pub async fn replace(&self, spool: &SPool) -> Result<Tweet,MyError> {
        let mut conn = spool.pool.get_conn().await?;

        let query = format!(r"REPLACE INTO {:}.tweet (created_time, text_body) VALUES (?, ?)",&spool.database);
        let p = Params::Positional(vec![self.created_time.clone().into(), self.text_body.clone().into()]);

        let qr = conn.exec_iter(
            query, p
        ).await?;

        let mut cp = self.clone();
        cp.tweet_id = qr.last_insert_id().unwrap() as u64;
        Ok(cp)
    }


    pub async fn update(&self, spool: &SPool) -> Result<(),MyError> {
        let mut conn = spool.pool.get_conn().await?;
        let query = format!(r"UPDATE `{:}`.tweet` SET created_time = ?, text_body = ? WHERE tweet_id = ? ", &spool.database);
        let p = Params::Positional(vec![self.created_time.clone().into(), self.text_body.clone().into(),  self.tweet_id.clone().into() ]);

        let qr = conn.exec_iter(
            query, p
        ).await?;

        Ok(())
    }

    pub async fn delete(&self, spool: &SPool) -> Result<(),MyError> {
        let mut conn = spool.pool.get_conn().await?;

        let query = format!(r"DELETE FROM `{:}`.tweet` WHERE tweet_id = ? ", &spool.database);
        let p = Params::Positional(vec![self.tweet_id.clone().into()]);

        conn.exec_drop(
            query, p
        ).await?;

        Ok(())
    }
}


